$(document).ready(function(){$('.carousel').owlCarousel({margin:20,loop:!0,autoplayTimeOut:2000,autoplayHoverPause:!1,responsive:{0:{items:1,nav:!1},600:{items:2,nav:!1},1000:{items:3,nav:!1}}})
var openCloseToggleButton=function(){if($(document).width()<=1071){var nav=$('.main-nav');nav.slideToggle(200)}}
$('.js--nav-icon').click(openCloseToggleButton)
$('.main-nav li a').click(openCloseToggleButton)
$(window).scroll(function(){if(this.scrollY>20){$('.navbar').addClass('sticky')}else{$('.navbar').removeClass('sticky')}
if(this.scrollY>500){$('.scroll-up-btn').addClass('show')}else{$('.scroll-up-btn').removeClass('show')}})
$('.scroll-up-btn').click(function(){$('html').animate({scrollTop:0})})})